sub storelink {
    my ($f, $line, $link) = @_;
    my $o = $link;

    if($link =~ /^\#/) {
        # ignore local-only links
        return;
    }
    # cut off any anchor
    $link =~ s:\#.*\z::;

    if($link =~ /^(https|http):/) {
        $url{$link} .= "$f:$line ";
        return;
    }

    # a file link
    my $dir = $f;
    $dir =~ s:([^/]*\z)::;

    while($link =~ s:^\.\.\/::) {
        $dir =~ s:([^/]*)\/\z::;
    }

    $flink{"./$dir$link"} .= "$f:$line ";
}

sub findlinks {
    my ($f) = @_;
    my $line = 1;
    open(F, "<:crlf", "$f") ||
        return;

    while(<F>) {
        if(/\]\(([^)]*)/) {
            my $link = $1;
            #print "$f:$line $link\n";
            storelink($f, $line, $link);
        }
        $line++;
    }
    close(F);
}

sub checkurl {
    my ($url) = @_;

    if($whitelist{$url}) {
        #print "$url is whitelisted\n";
        return 0;
    }

    print "check $url\n";
    my $curlcmd="curl -ILfsm10 --retry 2 --retry-delay 5 -A \"Mozilla/curl.se link-probe\"";
    my @content = `$curlcmd \"$url\"`;
    if(!$content[0]) {
        print STDERR "FAIL\n";
        return 1; # fail
    }
    return 0; # ok
}

for my $f (@files) {
    chomp $f;
    findlinks($f);
}

my $error;

for my $u (sort keys %url) {
    my $r = checkurl($u);

    if($r) {
        for my $f (split(/ /, $url{$l})) {
            printf "%s ERROR links to missing URL %s\n", $f, $u;
            $error++;
        }
