}

for my $l (sort keys %flink) {
    if(! -r $l) {
        for my $f (split(/ /, $flink{$l})) {
            printf "%s ERROR links to missing file %s\n", $f, $l;
            $error++;
        }
    }
}

exit 1 if ($error);

